    components  = {"": "", "6": "console", "7": "readline", "8": "e2p", "9": "parted-1", "10": "pcprofile"}
    milestones  = {"": "", "5": "GCC-milestone2", "6": "GCC-milestone3", "7": "GCC-milestone1", "8": "GCC-milestone6", "9": "GCC-milestone7", "10": "GCC-milestone4", "11": "GCC-milestone5", "12": "GCC-milestone8", "13": "GCC-milestone9"}
    versions    = {"": "", "5": "GCC-ver1"}

    tck_typenames= ["blueprint", "clarification", "defect", "document", "enhancement", "feature"]
    tck_severitynames= ["blocker", "critical", "major", "minor", "normal", "trivial"]
    projusers   = ["indudeepdk", "sabeerpb", "abhilashabg", "balakrishnanjk", "halimaaf", "abhahi", "ajantagc", "rabinderuc"]

    tckccodes   = [
