    function setup_ticket() {
        var div_zticketpage= dojo.query( 'div.zticketpage' )[0];
        var div_tcomments  = dojo.query( 'div#tcomments' )[0];

        /* Setup the wiki goto list */
        select_goto( dojo.query( '#selectticket' )[0] );

        make_ifrs_tckcomments( '/p/GCC/t/337?jsonobj=tckcomments&view=js',
                               items_tckcomments );
        make_ifrs_tckrcomments( '/p/GCC/t/337?jsonobj=tckrcomments&view=js' );

        /* Attachments */
        new zeta.Attachments(
                { user: [ '1', 'admin' ],
                  id: 'tckattachblk',
                  addform: [ 'addtckattachs', '/p/GCC/t/337?form=submit&formname=addtckattachs&view=js' ],
                  delform: [ 'deltckattachs', '/p/GCC/t/337?form=submit&formname=deltckattachs&view=js' ],
                  attachon: [ '337', 'ticket_id' ],
                  editable: 1,
                  url: '/p/GCC/t/337?jsonobj=tckattachs&view=js',
                  attachs: {}
                }, dojo.query( "div[name=tattachblk]" )[0]
            )
        /* Tags */
        new zeta.Tags(
                { user: [ '1', 'admin' ],
                  id: 'tcktagblk',
                  addform: [ 'addtcktags', '/p/GCC/t/337?form=submit&formname=addtcktags&view=js' ],
                  delform: [ 'deltcktags', '/p/GCC/t/337?form=submit&formname=deltcktags&view=js' ],
                  tagon: [ '337', 'ticket_id' ],
                  editable: 1,
                  url: '/p/GCC/t/337?jsonobj=tcktags&view=js',
                  tags: {"PC": "PC"}
                }, dojo.query( "div[name=ttagblk]" )[0]
            )
        /* Comment list */
        new zeta.CommentContainer({
                ifrs_comments: tckcomments,
                ifrs_rcomments: tckrcomments,
                crformid: 'createtcmt',
                rpformid: 'replytcmt',
                edformid: 'updatetcmt',
                sortby: 'ticket_comment_id',
                identity: 'ticket_comment_id'
            }, div_tcomments );

        // Widgetify forms for inline editing.
        if( true ) {
            new zeta.Form({ normalsub: true, formid: 'tcksummary' });
            new zeta.Form({ normalsub: true, formid: 'tcktype' });
            new zeta.Form({ normalsub: true, formid: 'tckseverity' });
            new zeta.Form({ normalsub: true, formid: 'tckcomponent' });
            new zeta.Form({ normalsub: true, formid: 'tckmilestone' });
            new zeta.Form({ normalsub: true, formid: 'tckversion' });
            new zeta.Form({ normalsub: true, formid: 'tckpromptuser' });
            new zeta.Form({ normalsub: true, formid: 'tckparent' });
            new zeta.Form({ normalsub: true, formid: 'tckblockedby' });
            new zeta.Form({ normalsub: true, formid: 'tckblocking' });
            new zeta.Form({ normalsub: true, formid: 'tckdescription' });

            function createtstat_onsubmit( e ) {
                submitform( form_createtstat, e );
                dojo.stopEvent(e);
                update_colorcode();
            }
            new zeta.Form({ onsubmit: createtstat_onsubmit,
                            formid: 'createtstat' });
        }

        /* Inline Editing */
        function update_colorcode() {
            var st = form_createtstat ?
                     dojo.query( 'select[name=tck_statusname]', form_createtstat
                             )[0].value
                     : null;
            var sv = form_tckseverity ?
                     dojo.query( 'input[name=tck_severityname]', form_tckseverity
                             )[0].value
                     : null;
            var ty = form_tcktype ?
                     dojo.query( 'input[name=tck_typename]', form_tcktype
                             )[0].value
                     : null;
            var tckdetail = {
                tck_statusname : st,
                tck_severityname : sv,
                tck_typename : ty,
            }
            var bgcolor = tckcolorcode( tckdetail, tckccodes );
            dojo.publish( 'tckbrd_chbg', [ 'tckboard', bgcolor ] );
        }

        var inlines = []
        if( true ) {
            inlines = dojo.query( 'span.inedit', div_zticketpage );
        }
        function inline_onchange( formnode, field, value ) {
            // Translate the component name into id
            if( field == 'component_id' ) {
                value = keyforvalue( components, value );
            } else if ( field == 'milestone_id' ) {
                value = keyforvalue( milestones, value );
            } else if ( field == 'version_id' ) {
                value = keyforvalue( versions, value );
            }
            dojo.query( 'input[name=' + field + ']', formnode 
                      )[0].value = value;
            submitform( formnode );
            update_colorcode();
        }
        dojo.forEach(
            inlines,
            function (item) {
                var name     = dojo.attr( item, 'name' );
                var formnode = null;
                if ( name == 'summary' ) {
                    new dijit.InlineEditBox({
                        editor : "dijit.form.TextBox",
                        onChange  : dojo.hitch(
                                        null, inline_onchange,
                                        form_tcksummary, 'summary' 
                                    ),
                        width : '40em'
                    }, item )        
                } else if ( name == 'tck_typename' ) {
                    var store = create_ifrs_store( tck_typenames );
                    new dijit.InlineEditBox({
                        editor : "dijit.form.FilteringSelect",
                        onChange  : dojo.hitch(
                                        null, inline_onchange,
                                        form_tcktype, 'tck_typename'
                                    ),
                        editorParams : { class: "fntbold", store: store }
                    }, item )        
                } else if ( name == 'tck_severityname' ) {
                    var store = create_ifrs_store( tck_severitynames );
                    new dijit.InlineEditBox({
                        editor : "dijit.form.FilteringSelect",
                        onChange  : dojo.hitch(
                                        null, inline_onchange,
                                        form_tckseverity, 'tck_severityname'
                                    ),
                        editorParams : { store : store }
                    }, item )        
                } else if ( name == 'component_id' ) {
                    var store = create_ifrs_store( values(components).sort() );
                    new dijit.InlineEditBox({
                        editor : "dijit.form.FilteringSelect",
                        onChange  : dojo.hitch(
                                        null, inline_onchange,
                                        form_tckcomponent, 'component_id'
                                    ),
                        editorParams : { store : store }
                    }, item )        
                } else if ( name == 'milestone_id' ) {
                    var store = create_ifrs_store( values(milestones).sort() );
                    new dijit.InlineEditBox({
                        editor : "dijit.form.FilteringSelect",
                        onChange  : dojo.hitch(
                                        null, inline_onchange,
                                        form_tckmilestone, 'milestone_id'
                                    ),
                        editorParams : { store : store }
                    }, item )        
                } else if ( name == 'version_id' ) {
                    var store = create_ifrs_store( values(versions).sort());
                    new dijit.InlineEditBox({
                        editor : "dijit.form.FilteringSelect",
                        onChange  : dojo.hitch(
                                        null, inline_onchange,
                                        form_tckversion, 'version_id'
                                    ),
                        editorParams : { store : store }
                    }, item )        
                } else if ( name == 'promptuser' ) {
                    var store = create_ifrs_store( projusers );
                    new dijit.InlineEditBox({
                        editor : "dijit.form.FilteringSelect",
                        onChange  : dojo.hitch(
                                        null, inline_onchange,
                                        form_tckpromptuser, 'promptuser'
                                    ),
                        editorParams : { store : store }
                    }, item )        
                } else if ( name == 'parent_id' ) {
                    new dijit.InlineEditBox({
                        editor : "dijit.form.TextBox",
                        onChange  : dojo.hitch(
                                        null, inline_onchange,
                                        form_tckparent, 'parent_id'
                                    )
                    }, item )        
                } else if ( name == 'blockedby_ids' ) {
                    new dijit.InlineEditBox({
                        editor : "dijit.form.TextBox",
                        onChange  : dojo.hitch(
                                        null, inline_onchange,
                                        form_tckblockedby, 'blockedby_ids'
                                    )
                    }, item )        
                } else if ( name == 'blocking_ids' ) {
                    new dijit.InlineEditBox({
                        editor : "dijit.form.TextBox",
                        onChange  : dojo.hitch(
                                        null, inline_onchange,
                                        form_tckblocking, 'blocking_ids'
                                    )
                    }, item )        
                } else if ( name == 'description' ) {
                    new dijit.InlineEditBox({
                        editor : "dijit.form.Textarea",
                        onChange  : dojo.hitch(
                                        null, inline_onchange,
                                        form_tckdescription, 'description'
                                    )
                    }, item )
                }
            }
        );

        /* Change status */
        dojo.connect(
            dojo.query( "span.togglest" )[0],
            'onclick',
            function( e ) {
                dojo.toggleClass( dojo.query( 'div.changest' )[0],
                                  'dispnone', false );
                dojo.toggleClass( dojo.query( 'div.viewst' )[0],
                                  'dispnone', true );
                dojo.stopEvent( e );
            }
        );
    }

        function contexttooltip() {
            var n_cont = dojo.byId( 'cont_tooltips' );
            var n_trgr = dojo.byId( 'trgr_tooltips' );
            if( n_cont ) {
                new zeta.ToolTips({
                    n_tooltip : n_trgr,
                    tooltips: [["Help", "Tickets are helpful to track bugs, tasks, requirements etc ...


The three main attributes of a ticket are type, status, severity.
Type should give an idea about why? and what? of a ticket. Status actually
tracks the flow of work or activity happening on a ticket. Severity, well tells
how severe the ticket is to the project (synonymous to priority).


Every ticket status change should provide the next status of the ticket and
due-date to act on the ticket state. The user who changes the ticket status
will become the new owner of the ticket.


If, in case the changed ticket status actually expects a response from a user
other than the ticket owner, it can be indicated so using 'promptuser'
attribute.


Use ticket id, where ever the ticket needs to be referenced.
"], ["Attachments", "Upload attachments by clicking on the iconized title. Clicking on the
same once again will hide it. Delete attachments by clicking on the cross-wire.
Upload any number of attachments files to ticket.



Every attached file, will have its "id" in paranthesis. Use the id value when
refering to the attachment.
"], ["Tags", "Tag a ticket by clicking on the iconized title. Delete tags by clicking on
the cross-wire. Tag names should be 2 or more characters
"], ["Favorites", "Registered users can pick ticket(s) as their favorite, provided the user
has got 'TICKET_VIEW' permission"], ["Vote", "Registered users can up-vote or down-vote a ticket."]]
                }, n_cont )
            }
        };

div.script:
/* Setup favorite ticket for user */
function initform_tckfav() {
    var n_span  = dojo.query( "span[name=favtck]" )[0];
    var w_form  = new zeta.Form({ normalsub: true, formid: 'tckfav' });
    var n_field = dojo.query( "input[name=addfavuser]", form_tckfav )[0];
    if( n_span && n_field ) {
        new ZFavorite( n_span, form_tckfav, n_field );
    }
}

/* Setup ticket voting form */
function initform_votetck() {
    var n_span = dojo.query( "span[name=tckvote]" )[0];
    if( n_span ) {
        new zeta.Voting({
            upvotes: 4,
            downvotes: 6,
            currvote: '',
            formid: 'votetck'
        }, n_span );
    }
}

body.script:
// Common initialization routine
function init_zeta() {
    dojo.subscribe( 'flash', null, flashmsghandler );
}

// Initialize quick-links
function qlinks_menu() {
    var qlMenu;
    var tgtnodes = dojo.query( 'div#metanav span[name=quick-links]' )[0];
    var quicklinks = [["users", "/u"], ["invite", "/u/inviteuser"]];

    if ( tgtnodes ) {
        pMenu = new zeta.Menu({
                        targetNodes   :[ tgtnodes ],
                        style: { fontSize: 'small', width: '6em', color: 'blue', }
                });
        dojo.forEach( quicklinks,
            function( ql ) {
                purl = '' + ql[0] + ''
                pMenu.addChild( new zeta.MenuItem(
                                    { content: purl, class: 'fntbold hoverhighlight' }
                              ));
            }
        );
    }
}

// Initialize project menu
function project_menu() {
    var pMenu;
    var tgtnodes = dojo.query( 'div#metanav span[name=myprojects]' )[0];
    var myprojects = [["GCC", "/p/GCC"], ["JavaScript", "/p/JavaScript"], ["dojo", "/p/dojo"], ["firefox", "/p/firefox"], ["mako", "/p/mako"], ["nose", "/p/nose"], ["ply", "/p/ply"], ["python", "/p/python"], ["sqlalchemy", "/p/sqlalchemy"]];

    if ( tgtnodes ) {
        pMenu = new zeta.Menu({
                        targetNodes   :[ tgtnodes ],
                        style: { fontSize: 'small',
                                 color: 'blue',
                               }
                });
        pMenu.addChild(
            new zeta.MenuItem(
                { content: 'New-project',
                  class: 'hoverhighlight' }
            )
        );
        pMenu.addChild( new zeta.MenuItem({ content: '' }));
        dojo.forEach( myprojects,
            function( p ) {
                purl = '' + p[0] + ''
                pMenu.addChild( new zeta.MenuItem(
                                    { content: purl, class: 'fntbold hoverhighlight' }
                              ));
            }
        );
        pMenu.addChild( new zeta.MenuItem({ content: '' }));
        pMenu.addChild( new zeta.MenuItem(
                            { content: 'List-all',
                              class: 'hoverhighlight'
                            }
                      ));
    }
}

dojo.addOnLoad( init_zeta );
dojo.addOnLoad( qlinks_menu );
dojo.addOnLoad( project_menu );

// Handlers for other elements in derived files.
dojoaddOnLoad( 'pagebartooltip' );
dojoaddOnLoad( 'contexttooltip' );

// Handler for search box.
dojo.addOnLoad( function() {
    dojo.forEach( dojo.query( "span[name=searchbox] input[type=text]" ),
        function( n ) {
            dojo.toggleClass( n, 'fggray2', true );
            dojo.connect( n, 'onfocus',
                          function( e ) {
                              dojo.attr( n, 'helpstr', n.value );
                              n.value = '';
                              dojo.toggleClass( n, 'fggray2', false );
                          }
                        );
            dojo.connect( n, 'onblur',
                          function( e ) {
                              n.value = dojo.attr( n, 'helpstr' );
                              dojo.toggleClass( n, 'fggray2', true );
                          }
                        );
        }
    );
});

dojo.addOnLoad( setup_ticket );
dojo.addOnLoad( function() {
    dojo.subscribe(
        'tckbrd_chbg',
        function( name, bgcolor ) {
            n = dojo.query( 'div[name='+name+']' )[0];
            dojo.style( n, { background : bgcolor });
        }
    );
});
dojoaddOnLoad( 'initform_tckfav' );
dojoaddOnLoad( 'initform_votetck' );

dojo.addOnLoad( setup_userpanes );
dojo.addOnLoad( adjust_upheight );
